catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    // If the asset value is blank
    if (newValue === '') {
        // Clear Current_disk_size field
        g_form.clearValue('variables.current_disk_size');
        return;
    }
    if (isLoading) {
        return;
    }
    // get the asset reference record
    var asset = g_form.getReference('asset', doUpdate);

    function doUpdate(asset) {
        // get the asset's disk size
        var selectedAssetDiskSize = asset.disk_size;
        // Update the disk_size in the catalog form
        g_form.setValue('variables.disk_size', selectedAssetDiskSize);
        // Get the disk_size label of currently selected asset
        var selectedAssetDiskSizeDisplayValue = g_form.getOption('variables.disk_size', selectedAssetDiskSize).text;
        // Update values in current_disk_size field and set it to read-only
        g_form.setReadOnly('variables.current_disk_size', true);
        g_form.setValue('variables.current_disk_size', selectedAssetDiskSizeDisplayValue);
    }
}
