sys_script_client.script:
function onLoad() {
	var getDep = new GlideAjax('GetUserDepartment');
	// Specify the getUserDepartment method
	getDep.addParam('sysparm_name','getUserDepartment');
	// Pass the Requested for sys_id
	getDep.addParam('sysparm_userID', g_user.userID);
	// Send the request to the server
	getDep.getXML(populateDepartmentField);

	// When the response is back from the server
	function populateDepartmentField(response){
		// Extract the Department from the response, clear any value from the department field, 
		// set new value in the Department field
		var departmentFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('hidden_department');
		g_form.setValue('hidden_department',departmentFromScriptInclude);
	}
}
