sys_script_include.script:
var ReferenceQualifierUtils = Class.create();
ReferenceQualifierUtils.prototype = {
    initialize: function() {
    },
    getWritableProjects: function() {
        var gr = new GlideRecord('x_465830_webinfosy_project');
        gr.query();
        var projectList = [];
        while (gr.next()) {
            if (gr.getValue('u_pname_4') == 'MAF' ) {
				projectList.push(gr.getValue('sys_id'));
            }
        }
		projectList = projectList.join(',');
        return 'sys_idIN' + projectList;
    },
    type: 'ReferenceQualifierUtils'
};
