sys_security_acl.script:
var answer = false;
var userID = gs.getUserID();
var userRecord = new GlideRecord("sys_user");
userRecord.get(userID);
var department = userRecord.department.sys_id;
var departmentCurrentUser = current.u_reference_department.sys_id;
var user = gs.getUser();
var isGroupMember = user.isMemberOf('Managed By') || user.isMemberOf('Supervised By');
if (department == departmentCurrentUser && isGroupMember) {
	answer = true;
}
